pragma solidity ^0.4.18;

contract QuickSort {

    function sort(uint256[] memory data) internal returns(uint256[] memory) {
       quickSort(data, uint256(0), uint256(data.length - 1));
       return data;
    }

    function quickSort(uint256[] memory arr, uint256 left, uint256 right) internal { 
        uint256 l = left;
        uint256 r = right;
        if(l==r) return;
        uint256 pivot = arr[uint256(left + (right - left) / 2)];
        while (l <= r) {
            while (arr[uint256(l)] < pivot) l++;
            while (pivot < arr[uint256(r)]) r--;
            if (l <= r) {
                (arr[uint256(l)], arr[uint256(r)]) = (arr[uint256(l)], arr[uint256(r)]);
                l++;
                r--;
            }
        }
        if (left < r)
            quickSort(arr, left, r);
        if (l < right)
            quickSort(arr, l, right);
    }
}

